/* eslint-disable import/no-cycle */
/* eslint-disable no-console */
import styles from './appDataWrapper.module.css';

import React, { Component } from 'react';

import { BrowserRouter as Router, Route } from 'react-router-dom';

// pages
import Event from '../../pages/event';
import Events from '../../pages/events';
import Home from '../../pages/home';
import Join from '../../pages/join';
import Login from '../../pages/login';
import Post from '../../pages/post';
import PostAdmin from '../../pages/postAdmin';
import PostForm from '../../pages/postForm';
// pages
import Profile from '../../pages/profile';
import Settings from '../../pages/settings';
import Users from '../../pages/users';
// components
import Footer from '../footer';
import Header from '../header';
import MochDatabase from '../mochDatabase';
import SideMenu from '../sideMenu';

class AppDataWrapper extends Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
    };
  }

  componentDidMount() {
    fetch('https://randomuser.me/api/?results=49')
      .then((res) => res.json())
      .then((data) => {
        this.setState({ users: data.results });
      });
  }

  render() {
    const { users } = this.state;
    return (
      <>
        <MochDatabase users={users}>
          <Router>
            <Header />
            <SideMenu />
            <main className={styles.content}>
              <Route exact path="/home">
                <Home users={users} />
              </Route>
              <Route exact path="/create-post" component={PostForm} />
              <Route exact path="/settings">
                <Settings users={users} />
              </Route>
              <Route exact path="/">
                <Login users={users} />
              </Route>
              <Route exact path="/join" component={Join} />
              <Route exact path="/posts/edit/:postId" component={PostForm} />
              <Route exact path="/posts/:postId" component={Post} />
              <Route exact path="/posts" component={PostAdmin} />
              <Route exact path="/users/profile/:uuid">
                <Profile users={users} />
              </Route>
              <Route exact path="/users">
                <Users users={users} />
              </Route>
              <Route exact path="/events" component={Events} />
              <Route exact path="/events/:eventId" component={Event} />
            </main>
            <Footer />
          </Router>
        </MochDatabase>
      </>
    );
  }
}

export default AppDataWrapper;
