/* eslint-disable prettier/prettier */
/* eslint-disable react/prop-types */
// import styles from './profile.module.css';

import React from 'react';

import { useParams } from 'react-router-dom';

import Loader from '../../components/loader/index';
import { useAxiosGet } from '../../hooks/HttpRequests';

function Profile() {
  const {  } = useParams();
  // Create your own Mock API: https://mockapi.io/
  const url = `https://randomuser.me/api/?uuid=${uuid}`;

  const profile = useAxiosGet(url);

  const user = profile.data.results;

  let content = null;

  if (user.error) {
    content = (
      <div>
        <div className="bg-blue-300 mb-2 p-3">
          If you see this error. Please remember to create your own
          {' '}
          <a href="https://randomuser.me/">Random User API</a>
          .
        </div>
        <div className="bg-red-300 p-3">
          There was an error please refresh or try again later.
        </div>
      </div>
    );
  }

  if (user.loading) {
    content = <Loader />;
  }

  if (user) {
    content = (
      <div>
        <h1 className="text-2xl font-bold mb-3">
          {user.name.firstname} 
          {' '}
          {user.name.lastname}
        </h1>
        <div>
          <img src={user.picture.medium} alt={user.name.firstname} />
        </div>
        <div className="font-bold text-lg mb-3">
          {user.login.username}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.gender}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.location.street}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.location.city}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.location.state}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.location.postcode}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.email}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.phone}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.cell}
        </div>
        <div className="font-bold text-lg mb-3">
          {user.dob}
        </div>
      </div>
    );
  }

  return <div className="container mx-auto">{content}</div>;
}

export default Profile;
