/* eslint-disable sort-destructure-keys/sort-destructure-keys */
import 'bulma/css/bulma.css';
import styles from './app.module.css';

import React from 'react';

import { BrowserRouter as Router, Route, useParams } from 'react-router-dom';

// components
import Footer from './components/footer';
import Header from './components/header';
import SideMenu from './components/sideMenu';
// pages
import Event from './pages/event';
import Events from './pages/events';
import Home from './pages/home';
import Join from './pages/join';
import Login from './pages/login';
import Post from './pages/post';
import PostAdmin from './pages/postAdmin';
import PostForm from './pages/postForm';
import Profile from './pages/profile';
import Settings from './pages/settings';
import Users from './pages/users';

function App() {
  const API_URL = 'https://randomuser.me/api/?results=50';
  const uuid = useParams();
  const PROFILE_API_URL = `https://randomuser.me/api/?uuid=${uuid}`;

  const { users, setUsers } = React.useState([]);

  const loadAPIData = async () => {
    const response = await fetch(API_URL);
    const data = await response.json();
    setUsers(data.results);
  };

  const { profile, setProfile } = React.useState([]);

  const loadAPIProfileData = async () => {
    const response = await fetch(PROFILE_API_URL);
    const profileData = await response.json();
    setProfile(profileData.results);
  };

  React.useEffect(() => {
    loadAPIData();
  });

  React.useEffect(() => {
    loadAPIProfileData();
  });

  return (
    <section className={styles.backgroundBody}>
      <section className={styles.container}>
        <Router>
          <Header />
          <SideMenu />
          <main className={styles.content}>
            <Route exact path="/" component={Home} />
            <Route exact path="/create-post" component={PostForm} />
            <Route exact path="/settings" component={Settings} />
            <Route exact path="/login" component={Login} />
            <Route exact path="/join" component={Join} />
            <Route exact path="/posts/edit/:postId" component={PostForm} />
            <Route exact path="/posts/:postId" component={Post} />
            <Route exact path="/posts" component={PostAdmin} />
            <Route exact path="/users/:uuid" component={Profile} />
            <Route exact path="/users">
              <Users users={users} />
            </Route>
            <Route exact path="/users">
              <Users users={users} />
            </Route>
            <Route exact path="/events" component={Events} />
            <Route exact path="/events/:eventId" component={Event} />
          </main>
          <Footer />
        </Router>
      </section>
    </section>
  );
}

export default App;
