import styles from './login.module.css';

import React, { useState } from 'react';

import { Button, Control, Field, Input, Label } from 'bloomer';

function Login({ users }) {
  const { event, setEvent } = useState({});
  const { user, setUser } = useState({});
  function handleInputChange() {
    // pull the name of the input and value of input out of the event object
    const {
      target: { name, value },
    } = event;
    // update the state to a key of the name of the input and value of the value of the input
    // ex: type: 'private'
    return setEvent({ [name]: value });
  }

  function onSubmit(e) {
    e.preventDefault();
    user = users[3];

  }

  return (
    <form onSubmit={onSubmit} className={styles.form}>
      <h1 className={styles.heading}>Login</h1>
      <Field>
        <Label className={styles.label}>Username</Label>
        <Control>
          <Input
            className={styles.input}
            name="username"
            value={username}
            onChange={handleInputChange}
          />
        </Control>
      </Field>
      <Field>
        <Label className={styles.label}>Password</Label>
        <Control>
          <Input
            className={styles.input}
            name="password"
            type="password"
            value={password}
            onChange={handleInputChange}
          />
        </Control>
      </Field>
      <Button className={styles.button} type="submit">
        Login
      </Button>
    </form>
  );
}

export default Login;
