import React, { Fragment } from "react";
import Status from "./Status";
import Icon from "../components/Icon/Icon";

import style from "../components/Message/Message.scss"

const Messages = {
  [Status.EMPTY]: ['Drop your photo here or tap to select.'],
  [Status.DRAGOVER]: ['Drop your photo'],
  [Status.INVALID_FILE_TYPE]: ['Only images allowed.', 'Drop your photo here or tap to select.'],
  [Status.INVALID_IMAGE_SIZE]: ['Your photo must be larger than 350px.', 'Drop your photo here or tap to select.']
}

const StatusMessage = {
  [Status.EMPTY]: translations => <p className={style["message__text"]}>{translations[0] || Messages[Status.EMPTY][0]}</p>,
  [Status.LOADING]: () => <Icon name="loading" size={48}/>,
  [Status.DRAGOVER]: translations => (
    <Fragment>
      <Icon name="upload" size={48}/>
      <p className={style["message__text"]}>{translations[0] || Messages[Status.DRAGOVER][0]}</p>
    </Fragment>
  ),
  [Status.INVALID_FILE_TYPE]: translations => (
    <Fragment>
      <p className={style["message__text"]}>{translations[0] || Messages[Status.INVALID_FILE_TYPE][0]}</p>
      <p className={style["message__text"]}>{translations[1] || Messages[Status.INVALID_FILE_TYPE][1]}</p>
    </Fragment>
  ),
  [Status.INVALID_IMAGE_SIZE]: translations => (
    <Fragment>
      <p className={style["message__text"]}>{translations[0] || Messages[Status.INVALID_IMAGE_SIZE][0]}</p>
      <p className={style["message__text"]}>{translations[1] || Messages[Status.INVALID_IMAGE_SIZE][0]}</p>
    </Fragment>
  ),
  [Status.LOADED]: () => null
};

export default StatusMessage;
